class Imageslider extends CI_Controller{
    function __construct()
    {
        parent::__construct(); 
        if (!$this->session->userdata('login')) {
            redirect('login','index');
        }
        $this->load->model('M_imageslider');
    } 

    /*
     * Listing of image_slider
     */
    function index()
    {
        $params['limit'] = RECORDS_PER_PAGE; 
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;
        
        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('imageslider/index?');
        $config['total_rows'] = $this->M_imageslider->get_all_image_slider_count();
        $this->pagination->initialize($config);

        $data['image_slider'] = $this->M_imageslider->get_all_image_slider($params);
        
        $data['_view'] = 'image_slider/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new image_banner
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   

                $image = time().'-'.str_replace(' ', '_', $_FILES["userfile"]['name']);

                $config = array(
                    'upload_path' => "./upload/images/",
                    'allowed_types' => "gif|jpg|png|jpeg|JPEG|JPG|PNG|GIF",
                    'overwrite' => TRUE,
                    'max_size' => "99999999999",
                    // 'max_height' => "800",
                    // 'max_width' => "1500",
                    'file_name' => $image
                    );
                    $this->load->library('upload', $config);

                if ( ! $this->upload->do_upload('userfile'))
                {
                        $error = array('error' => $this->upload->display_errors());
                                    $this->session->set_flashdata('Message',$error);
                        $data['_view'] = 'image_slider/add';
                        $this->load->view('layouts/main',$data);
                }
                else
                {
                        $data = array('upload_data' => $this->upload->data());
                        $params = array(
                            'slider_url' => base_url()."upload/imageslider/".$image,
                            'slider_file_name' => $image,
                            'created_at' => date('Y-m-d H:i:s'),
                            'created_by' => $this->session->userdata('admin_id'),
                            'start_date' => date('Y-m-d',strtotime($this->input->post('start_date')))." 00:00:00",
                            'end_date' => date('Y-m-d',strtotime($this->input->post('end_date')))." 23:59:59",
                            'active_status' => "1",
                        );
                        print_r($params); die;
                        
                        $image_banner_id = $this->M_imageslider->add_image_banner($params);
                        redirect('imageslider/index');

                }


            
        }
        else
        {            
            $data['_view'] = 'image_slider/add';
            $this->load->view('layouts/main',$data);
        }
    }  

