    /*
     * Deleting image_banner
     */
    function remove($slider_id)
    {
        $image_banner = $this->M_imageslider->get_image_banner($slider_id);

        // check if the image_banner exists before trying to delete it
        if(isset($image_banner['slider_id']))
        {
            $this->M_imageslider->delete_image_banner($slider_id);
            redirect('imageslider/index');
        }
        else
            show_error('The image_banner you are trying to delete does not exist.');
    }
    
