    /*
     * Get desa by desa_id
     */
    function get_desa($desa_id)
    {
        $desa = $this->db->query("
            SELECT
                *

            FROM
                `mstdesa`

            WHERE
                `desa_id` = ?
        ",array($desa_id))->row_array();

