        ")->row_array();

        return $mstdesa['count'];
    }
        
    /*
     * Get all mstdesa
     */
    function get_all_mstdesa($params = array())
    {
        $limit_condition = "";
        if(isset($params) && !empty($params))
            $limit_condition = " LIMIT " . $params['offset'] . "," . $params['limit'];
        
        $mstdesa = $this->db->query("
            SELECT
                *

            FROM
                `mstdesa` a 
                join mstkecamatan b on b.kecamatan_id = a.kecamatan_id

            WHERE
                1 = 1

            ORDER BY `desa_id` DESC

        ")->result_array();

        return $mstdesa;
    }
        
